           * let cnv;
           * let d;
           * let g;
           * function setup() {
           *   cnv = createCanvas(100, 100);
           *   cnv.mouseWheel(changeSize); // attach listener for
           *   // activity on canvas only
           *   d = 10;
           *   g = 100;
