literals:
mutation AppExtension($input: CreateAppExtensionInput!) {
            appExtension {
                createAppExtension(input: $input) {
                appExtension {
                    id
                    context
                    label {
                    defaultValue
                    locales {
                        value
                        localeCode
                        }
                    }
                    model
                    url
                    }
                }
            }
        }

query {
                store {
                    appExtensions {
                        edges {
                            node {
                                id
                            }
                        }
                    }
                }
            }
