literals:
.tags-list{
      display: flex;
      gap: 2px;
    }

    .tag, .add-tag{
      color: white;
      padding: .125em;
      border-radius: .75em;
      border: 1px solid var(--color-secondary);
      background: var(--color-highlight);
    }
    .tag:hover, .tag.selected{
      background-color: var(--color-secondary);
    }

    .tag .tag-name{
      padding-left: .375em;
      cursor: pointer;
    }

    .tag .tag-name:last-child{
      padding-right: .375em;
    }

    .tag .tag-delete{
      display: inline;
      background-color: var(--color-highlight);
      color: white;
      border: 1px solid transparent;
      border-radius: .75em;
      padding: 1px 4.5px;
      margin: 1px;
      box-sizing: border-box;
      cursor: pointer;
      transition: background-color .1s ease;
    }

    .tag .tag-delete:hover{
      background-color: var(--color-secondary);
      border-color: var(--color-secondary-light);
    }

    .add-tag{
      position: relative;
    }

    .add-tag input{
      color: inherit;
      border: none;
      background: none;
      height: 100%;
      box-sizing: border-box;
    }

    .add-tag input:focus-visible{
      outline: none;
    }

    .add-tag input[type="text"]{
      max-width:12ch;
    }

    .add-tag input[type="submit"]{
      cursor: pointer;
      opacity: 0;
    }

    .add-tag:valid input[type="submit"]{
      opacity: 1;
    }

    .add-tag input[type="submit"]:hover{
      color: var(--color-success);
      background-color: var(--color-secondary); 
      border-radius: .75em;
    }
