literals:
.tasks {
         display: inline-flex;
      }

      .task {
        width:        250px;
        height:       75px;
        border:       1px solid black;
        margin-right: 10px;
      }

      .task:last-child {
        margin-right: inherit;
      }

      .selected {
        color:      blue;
        background-color: green !important;
      }

      .highlight {
        background-color: yellow;
      }
