literals:
header {
      background-color: var(--md-comp-app-bar-color);
      color: var(--md-comp-app-bar-on-color);
      border-bottom: 1px solid var(--md-sys-color-outline);
      display: flex;
      align-items: center;
      --padding: 30px;
      height: var(--header-height);
      width: 100%;
      z-index: 10;
      overflow-x: auto;
    }
    .title {
      font-family: Google Sans;
      font-size: 16px;
      line-height: 19px;
      letter-spacing: 0em;
      text-align: left;
      white-space: nowrap;
      cursor: pointer;
    }
    .action {
      padding-left: 4px;
    }
    .pad-left {
      padding-left: var(--padding);
    }
    .pad-right {
      padding-right: var(--padding);
    }
    .spacer {
      flex: 1;
    }
    .info {
      padding-right: calc(var(--padding) / 2);
    }
    .desktop-only {
      display: none;
    }
    .mobile-only {
      display: block;
    }

    @media (min-width: 600px) {
      .desktop-only {
        display: block;
      }
      .mobile-only {
        display: none;
      }
    }
